local rx, ry = getResolution()
local vw = rx/100
local vh = ry/100

local Back = createLayer()
local BackOutline = createLayer()
local Center = createLayer()
local Ship = createLayer()
local Front = createLayer()
local FrontOutline = createLayer()

local radius = 0.35*ry/2
local theta = 5*getTime() 

for ii = 1,#V do
    C = V[ii]
    
    C1 = C[1]*math.cos(math.rad(theta)) - C[3]*math.sin(math.rad(theta))
    C3 = C[1]*math.sin(math.rad(theta)) + C[3]*math.cos(math.rad(theta))

    V[ii][1] = C1
    V[ii][3] = C3
end

for ii = 1,#P1 do 
    if V[P1[ii]][3] >= 0 then
        setNextStrokeColor(Front,0/255, 75/255, 255/255, 1)
        setNextStrokeWidth(Front,0.05*vh)
        setNextShadow(Front, 0.5*vh,0/255, 75/255, 255/255, 1)
        addLine(Front,V[P1[ii]][1]*radius + rx/2,V[P1[ii]][2]*radius + ry/2,V[P2[ii]][1]*radius+ rx/2,V[P2[ii]][2]*radius + ry/2)
