        setNextStrokeWidth(Back,0.05*vh)
        setNextShadow(Back, 0.5*vh,0/255, 75/255, 255/255,1)
        addLine(Back,V[P1[ii]][1]*radius + rx/2,V[P1[ii]][2]*radius + ry/2,V[P2[ii]][1]*radius + rx/2,V[P2[ii]][2]*radius + ry/2)
    end
end

